import Escrow from './artifacts/src/contracts/Escrow.sol/Escrow';
import {ethers} from 'ethers';
import './App.css';

const provider = new ethers.providers.Web3Provider(window.ethereum);

async function deploy(arbiter, beneficiary, value) {
  await window.ethereum.request({ method: 'eth_requestAccounts' });
  console.log('hello?');
  const signer = provider.getSigner();
  const factory = new ethers.ContractFactory(Escrow.abi, Escrow.bytecode, signer);
  return factory.deploy(arbiter, beneficiary, { value });
}

async function newContract() {
  if (typeof window.ethereum !== 'undefined') {
    let arbiter = "0x7fbA8b9606674E026D1Ae4468AadB9aCEfD41ae2";
    let beneficiary = "0x6A5c4ceA696700C6e614941e9bBa165271098215";
    let value = ethers.BigNumber.from("300000000000000000");
    const contract = await deploy(arbiter, beneficiary, value);
    console.log('ga sampe?');
    console.log(contract);
  } else {
    console.log('ethereum undefined');
  }

}



function App() {
  return (
    <div className="App">
      App
      <button onClick={newContract}>Set Greeting</button>
    </div>
  );
}

export default App;
