import Escrow from './artifacts/src/contracts/Escrow.sol/Escrow';
import {ethers} from 'ethers';
import './App.css';

const provider = new ethers.providers.Web3Provider(window.ethereum, "any");

async function deploy(arbiter, beneficiary, value) {
  const accounts = await window.ethereum.request({ method: 'eth_requestAccounts' });
  console.log(accounts);
  //await provider.send("eth_requestAccounts", []);
  console.log('hello?');
  const signer = provider.getSigner();
  console.log("Account:", await signer.getAddress());
  const factory = new ethers.ContractFactory(Escrow.abi, Escrow.bytecode, signer);
  return factory.deploy(arbiter, beneficiary, { value });
}

async function newContract() {
  if (typeof window.ethereum !== 'undefined') {
    //let arbiter = "0x7fbA8b9606674E026D1Ae4468AadB9aCEfD41ae2";
    let arbiter = "0xEE344dcB087D1bfEc402B815E7Bb3BB686701500";
    //let beneficiary = "0x8F6E16d717fb162C20aC6AC994cCbc44E6804EE9";
    let beneficiary = "0xbC227F22124b12a5770D81B2a23e65d2c03f7d93";
    let value = ethers.BigNumber.from("300000000000000000");
    const contract = await deploy(arbiter, beneficiary, value);
    console.log('ga sampe?');
    console.log(contract);
  } else {
    console.log('ethereum undefined');
  }

}

 provider.send("eth_requestAccounts", []);


function App() {
  return (
    <div className="App">
      App 1 
      <button onClick={newContract}>Set Greeting</button>
    </div>
  );
}

export default App;
